import { mapActions } from "vuex"
export default {
  data() {
    return {
      username: "",
      password: ""
    };
  },
  methods: {
    ...mapActions(["changeUsername"]),
    handleLogin(){
      if(this.username!=""&&this.password!=""){
        if(JSON.parse(localStorage.getItem(this.username))&& JSON.parse(localStorage.getItem(this.username))[0].password == this.password){
          console.log(this.$store)
          this.changeUsername(this.username);
          this.$router.push("/me");
          alert("登录成功");
        }else{
          alert("输入的用户名或密码错误!");
        }
      }else{
        alert("输入的用户名或密码为空");
      }
    },
    handleback(){
      this.$router.go(-1);
    }
    
  },
};
