@import "../../css/element.scss";
.content {
  text-align: center;
  .header {
    @include flex(row);
    justify-content: space-between;
    background: #fff;
    height: 80px;
    line-height: 80px;
    i {
      width: 10%;
      text-align: center;
      font-size: 30px;
    }
    span {
      font-size: 30px;
      color: #ef7d00;
      padding-right: 15px;
    }
  }
  h4 {
    width: 100%;
    text-align: center;
    padding-top: 80px;
    padding-bottom: 40px;
    color: #666;
  }
  .box {
    @include flex(row);
    justify-content: space-between;
    padding: 0 25px;
    margin: 50px 0;
    height: 80px;
    box-sizing: border-box;
    .title {
      width: 10%;
      font-size: 0;
      text-align: center;
      position: relative;
      img {
        width: 100%;
        position: absolute;
        top: 50%;
        left: 0;
        transform: translateY(-50%);
      }
    }
    .num {
      width: 85%;
      overflow: hidden;
      color: #888;
      line-height: 80px;
      font-size: 24px;
      border: 0;
      border-radius: 20px;
      padding-left: 20px;
      // background-color: rgb(243, 243, 243);
    }
    .left {
      width: 30%;
      font-size: 28px;
      line-height: 80px;
      input {
        width: 30px;
        height: 30px;
      }
    }
    .right {
      width: 45%;
      font-size: 28px;
      line-height: 80px;
      em {
        color: #ef7d00;
      }
    }
  }
  button {
    width: 90%;
    height: 80px;
    text-align: center;
    background: #ef7d00;
    color: #fff;
    font-size: 35px;
    line-height: 80px;
    border: 0;
    border-radius: 10px;
  }
}
