import { mapState, mapMutations } from "vuex";

export default {
  data() {
    return {
      //存储的数据
      goodsList: [],
      checked: false,
      checkedList: []
    };
  },
  methods: {
    //点击减号
    handleEle(index) {
      let item = this.goodsList[index];
      if (item.num < 1) {
        return;
      }
      item.num--;
      this.$emit("changeTotal", this.totalPrice, this.totalNum);
    },
    //点击加号
    handleAdd(index) {
      let item = this.goodsList[index];
      item.num++;
      this.$emit("changeTotal", this.totalPrice, this.totalNum);
    },
    //点击全选
    handleSwapCheck() {
      let selectList = this.$refs.selectList;
      console.log(selectList);
      const len = selectList.length;
      if (this.checked) {
        for (let i = 0; i < len; i++) {
          let item = selectList[i];
          item.checked = false;
        }
        this.checked = false;
        this.checkedList = [];
      } else {
        for (let i = 0; i < len; i++) {
          let item = selectList[i];
          if (item.checked === false) {
            item.checked = true;
            this.checkedList.push(selectList[i].value);
            console.log(this.checkedList);
          }
        }
        this.checked = true;
      }
      this.$emit("changeTotal", this.totalPrice, this.totalNum);
    },
    //点击子选择框
    handleCheckedList() {
      let selectList = this.$refs.selectList;
      const len = selectList.length;

      let j = 0;
      for (let i = 0; i < len; i++) {
        let item = selectList[i];
        if (item.checked) {
          j++;
        } else {
          j--;

          // console.log(this.checkedList);
        }
      }
      selectList.forEach((el, i) => {
        if (el.checked) {
          let index = this.checkedList.indexOf(el.value);
          // console.log(index)
          if (index == -1) {
            //  console.log(1);
            this.checkedList.push(el.value);
          }
        } else {
          let index = this.checkedList.indexOf(el.value);
          if (index > -1) {
            this.checkedList.splice(index, 1);
          }
        }
      });
      this.$emit("changeTotal", this.totalPrice, this.totalNum);
      if (j == len) {
        this.checked = true;
      } else {
        this.checked = false;
      }
    },
    //点击删除
    handleDelete() {
      const selectList = this.$refs.selectList;
      let setlocalStorage = [];
      let goodsObject = {
        img: "",
        title: "",
        price: "",
        num: ""
      };

      let arr0 = [];
      selectList.forEach((el, index) => {
        if (el.checked) {
          arr0.push(index);

          // console.log(index)
          // console.log(index)
          // goodsObject.img = this.goodsList[index].img;
          // goodsObject.title = this.goodsList[index].title;
          // goodsObject.price = this.goodsList[index].price;
          // goodsObject.num = this.goodsList[index].num;
          // this.changeDelete(goodsObject);
          // this.goodsList = JSON.parse(localStorage.getItem("goods"));
          // el.checked = false;
        }
        el.checked = false;
      });

      console.log(arr0);
      this.changeDelete(arr0);
      this.goodsList = JSON.parse(localStorage.getItem("goods"));
      this.$emit("changeTotal", this.totalPrice, this.totalNum);
    },
    ...mapMutations(["changeDelete"])
  },
  mounted() {
    if (localStorage.goods) {
      this.goodsList = JSON.parse(localStorage.getItem("goods"));
    }
  },
  computed: {
    // ...mapState(["goodsList"])
    //计算总价格
    totalPrice() {
      let total = 0;
      for (let i = 0; i < this.checkedList.length; i++) {
        const index = this.checkedList[i];
        const item = this.goodsList[index];
        if (item) {
          total += parseInt(item.price) * parseInt(item.num);
        } else {
          continue;
        }
      }
      return total;
    },
    //计算总数量
    totalNum() {
      let num = 0;
      for (let i = 0; i < this.checkedList.length; i++) {
        const index = this.checkedList[i];
        const item = this.goodsList[index];
        if (item) {
          num += parseInt(item.num);
        } else {
          continue;
        }
      }
      return num;
    }
  }
};
