@import "../../css/element.scss";
.panel {
  @include panel;
  .content {
    @include flex;
    .item {
      margin-bottom: 40px;
      > img {
        display: block;
        width: 100%;
        height: 170px;
      }
      .list {
        @include flex(row);
        .product {
          width: 50%;
          height: 396px;
          box-sizing: border-box;
          background: rgba(0, 0, 0, 0.02);
          text-align: center;
          position: relative;
          a{
            text-decoration: none;
          }
          img {
            width: 180px;
            height: 180px;
            margin: 44px auto 20px;
          }
          .title {
            font-family: PingFangSC-Medium;
            font-size: 28px;
            color: #444;
            overflow: hidden;
            text-overflow: ellipsis;
            white-space: nowrap;
          }
          .price {
            font-family: PingFangSC-Semibold;
            font-size: 20px;
            color: #999;
            height: 48px;
            line-height: 48px;
            em {
              font-size: 36px;
              color: #ff3232;
              margin-right: 4px;
              &::before {
                content: "ï¿¥";
                font-size: 28px;
              }
            }
          }
          article {
            @include flex(row);
            align-items: center;
            justify-content: space-around;
            padding: 0 20px;
            height: 30px;
            line-height: 30px;
            margin-top: 16px;
            .wrapper {
              width: 144px;
              height: 10px;
              background: #f6e6e6;
              border-radius: 22px;
              .inner {
                max-width: 100%;
                height: 10px;
                background-image: linear-gradient(
                  270deg,
                  #f55 1%,
                  #ff9c31 100%
                );
                border-radius: 22px;
              }
            }
            p {
              font-size: 24px;
              color: #999;
            }
          }
          label {
            position: absolute;
            left: 0;
            top: 0;
            font-size: 20px;
            color: #ff0000;
            text-align: center;
            padding: 4px 8px;
            background-image: linear-gradient(-35deg, #c1c1c1 0, #acacac 100%);
          }
        }
      }
    }
  }
}
